import React, { useEffect, useState } from "react";
import api from "../services/api";
import { WiDaySunny, WiRain, WiCloud, WiHumidity } from "react-icons/wi";

// Liste des r√©gions avec images (liens Unsplash pour toutes)
const regionsSenegal = [
  { nom: "Dakar", img: "data:image/webp;base64," },
  { nom: "Diourbel", img: "https://encrypted-tbn3.gstatic.com/licensed-image?q=tbn:ANd9GcRjZAqJnYM9QZ0LSy-enWcuIIKHvoU-OvRiePr1idSkcAAynyOwbllPX7ykvh99BR0C7ZLaBfVnNnXEoD_72VBAcgtqtOw1RixW5bfoiA" },
  { nom: "Fatick", img: "data:image/jpeg;base64," },
  { nom: "Kaffrine", img: "data:image/jpeg;base64," },
  { nom: "Kaolack", img: "data:image/jpeg;base64," },
  { nom: "K√©dougou", img: "data:image/jpeg;base64," },
  { nom: "Kolda", img: "https://encrypted-tbn0.gstatic.com/licensed-image?q=tbn:ANd9GcQJHDF4ois542BmWboMF3CwQmYyNTrvL8IuiF5y4O4rJ5EvMlY1XiykZm-GkUZ9scgqj95wwmrt1xobanNBl955M6tXFa-Z3zut7f-Gnjk" },
  { nom: "Louga", img: "data:image/jpeg;base64," },
  { nom: "Matam", img: "data:image/jpeg;base64," },
  { nom: "Saint-Louis", img: "data:image/jpeg;base64," },
  { nom: "S√©dhiou", img: "data:image/jpeg;base64," },
  { nom: "Tambacounda", img: "data:image/jpeg;base64," },
  { nom: "Thi√®s", img: "https://encrypted-tbn1.gstatic.com/licensed-image?q=tbn:ANd9GcSv3UcrqAb0IA_OhCzpHyRz7ckDPwcdj0tLuL6U4bOAku7TyCnMcizx8VEH_wP32EHsb-lYfOj5kCiySbQavxZFTBbFClnPIHs6gu3ZMRM" },
  { nom: "Ziguinchor", img: "https://encrypted-tbn2.gstatic.com/licensed-image?q=tbn:ANd9GcRjlpxLwOmZTptv8kW5DamJC61SDYCl152nBQBXfPv_s0YrLY3mKIp9rsQw6iRjL8mfIh3t6m8EuQlml8fs-a-X8-RrcE9xHCOGag3J4Q" }
];

// Carte du S√©n√©gal (image SVG libre de droits)
const carteSenegalUrl = "data:image/jpeg;base64,";

export default function Home() {
  const [meteoData, setMeteoData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [regionActive, setRegionActive] = useState(regionsSenegal[0]);
  const [carouselIndex, setCarouselIndex] = useState(0);

  // Slider automatique
  useEffect(() => {
    const interval = setInterval(() => {
      setCarouselIndex((prev) => (prev + 1) % regionsSenegal.length);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const fetchMeteo = async () => {
      setLoading(true);
      try {
        const res = await api.get("/meteo?region=" + regionActive.nom);
        setMeteoData(res.data);
      } catch (err) {
        console.error("Erreur de chargement m√©t√©o :", err);
      } finally {
        setLoading(false);
      }
    };
    fetchMeteo();
  }, [regionActive]);

  const getWeatherIcon = (temp) => {
    if (temp >= 30) return <WiDaySunny size={32} />;
    if (temp >= 20) return <WiCloud size={32} />;
    return <WiRain size={32} />;
  };

  return (
    <div className="container mt-4">
      {/* Titre de bienvenue */}
      <h2 style={{ textAlign: "center", marginBottom: 32, fontWeight: "bold" }}>
        Bienvenue sur Sunu M√©t√©o S√©n√©gal
      </h2>

      {/* Ligne principale */}
      <div style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        gap: 32,
        flexWrap: "wrap"
      }}>
        {/* Carrousel gauche */}
        <div style={{ width: 250, minWidth: 200 }}>
          <img
            className="animated-photo"
            src={regionsSenegal[carouselIndex].img}
            alt={regionsSenegal[carouselIndex].nom}
            style={{
              width: "100%",
              height: 150,
              objectFit: "cover",
              borderRadius: 12,
              boxShadow: "0 2px 8px #0001"
            }}
          />
          <div style={{
            textAlign: "center",
            fontWeight: "bold",
            marginTop: 8,
            color: "#1976d2"
          }}>
            {regionsSenegal[carouselIndex].nom}
          </div>
        </div>

        {/* Carte du S√©n√©gal */}
        <div style={{ minWidth: 260, maxWidth: 320, flex: 1, textAlign: "center" }}>
          <img
            className="animated-photo"
            src={carteSenegalUrl}
            alt="Carte du S√©n√©gal"
            style={{
              width: "100%",
              maxWidth: 320,
              height: 220,
              objectFit: "contain",
              borderRadius: 12,
              border: "2px solid #1976d2",
              background: "#fff"
            }}
          />
        </div>

        {/* Carrousel droit */}
        <div style={{ width: 250, minWidth: 200 }}>
          <img
            className="animated-photo"
            src={regionsSenegal[(carouselIndex + 1) % regionsSenegal.length].img}
            alt={regionsSenegal[(carouselIndex + 1) % regionsSenegal.length].nom}
            style={{
              width: "100%",
              height: 150,
              objectFit: "cover",
              borderRadius: 12,
              boxShadow: "0 2px 8px #0001"
            }}
          />
          <div style={{
            textAlign: "center",
            fontWeight: "bold",
            marginTop: 8,
            color: "#1976d2"
          }}>
            {regionsSenegal[(carouselIndex + 1) % regionsSenegal.length].nom}
          </div>
        </div>
      </div>

      {/* Affiche personnalis√©e */}
      <div
        style={{
          width: "100%",
          background: "#1976d2",
          color: "#fff",
          borderRadius: "16px",
          padding: "40px 0",
          margin: "32px 0 1.5rem 0",
          textAlign: "center",
          fontSize: "2rem",
          fontWeight: "bold",
          boxShadow: "0 2px 8px #0002"
        }}
      >
        Il fait beau aujourd'hui ‚òÄÔ∏è<br />
        Connecte-toi avant de sortir !
      </div>

      {/* S√©lecteur de r√©gion */}
      <div className="mb-3" style={{ display: "flex", gap: 8, overflowX: "auto" }}>
        {regionsSenegal.map((region) => (
          <button
            key={region.nom}
            className={`btn btn-sm ${regionActive.nom === region.nom ? "btn-primary" : "btn-outline-primary"}`}
            onClick={() => setRegionActive(region)}
            style={{ minWidth: 100 }}
          >
            {region.nom}
          </button>
        ))}
      </div>

      {/* Slider m√©t√©o */}
      <div
        style={{
          background: "#fff",
          borderRadius: "10px",
          boxShadow: "0 2px 8px #0001",
          padding: "16px",
          marginBottom: "2rem",
          overflowX: "auto"
        }}
      >
        <div style={{ display: "flex", gap: 16, minWidth: 600 }}>
          {loading ? (
            <span>Chargement...</span>
          ) : meteoData.length === 0 ? (
            <span>Aucune donn√©e m√©t√©o disponible.</span>
          ) : (
            meteoData.map((item, idx) => (
              <div
                key={item._id || idx}
                style={{
                  minWidth: 90,
                  textAlign: "center",
                  borderRight: idx !== meteoData.length - 1 ? "1px solid #eee" : "none"
                }}
              >
                <div style={{ fontWeight: "bold" }}>
                  {new Date(item.date).toLocaleDateString("fr-FR", { weekday: "short", day: "2-digit" })}
                </div>
                <div>{getWeatherIcon(item.temperature)}</div>
                <div style={{ fontSize: 18, color: "#1976d2" }}>{item.temperature}¬∞C</div>
                <div style={{ fontSize: 13 }}>
                  <WiHumidity size={16} /> {item.humidite}%
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}
